mapper.select:
SELECT rownum, cls.cls_no, cls.cls_name, cls.cls_kind, cls.cls_cnt, cls.cls_days, cls.cls_s_date , cls.cls_e_date, cls.cls_price, cls.cls_state, cls.cls_info , cls.cls_stime, cls.cls_etime, cls.cls_day , tch.tch_name , type.type_name , decode(evt.evt_name,'없음','NO','YES') as evt_dName , evt.evt_seq, evt.evt_name, evt.evt_cont, evt.evt_s_date, evt.evt_e_date, evt.evt_cond FROM class cls, teacher tch, type type, event evt WHERE cls.tch_no = tch.tch_no AND cls.type_no = type.type_no AND cls.evt_seq = evt.evt_seq
AND cls.cls_state = #{progress}
AND cls.cls_no = #{cls_no}

SELECT not_date, not_time, notice_no, not_title, not_cont, gym_no FROM notice
AND gym_no = #{gym_no}
AND notice_no = #{notice_no}
AND not_title LIKE '%'||#{not_title}||'%'
AND not_cont LIKE '%'||#{not_cont}||'%'
ORDER BY notice_no DESC

SELECT gym_no, gym_name
  FROM gym
 WHERE rownum = 1
   AND gym_id = #{gym_id}
   AND gym_pw = #{gym_pw}

SELECT filename, filesize, filedata
  FROM images
 WHERE type = 'gym'

   AND file_seq = #{file_seq}


   AND typecode = #{typecode}

SELECT filedata
  FROM images
 WHERE file_seq = #{file_seq}

mapper.insert:
		INSERT INTO gym (gym_no 
		                ,gym_id ,gym_pw ,gym_username ,gym_usertell
                        ,gym_name ,gym_tel     ,gym_addr   ,gym_addr_dtl ,gym_zipcode
                        ,gym_number ,gym_info    ,gym_lat    ,gym_lng      
                        ,gym_parking ,gym_wash   ,gym_uniform ,gym_locker ,gym_like )
                  VALUES((SELECT max(gym_no)+1 FROM gym)
                        ,#{j_gym_id} ,#{j_gym_pw} ,#{gym_username} ,#{gym_usertell} 
                        ,#{gym_name}   ,#{gym_tel} ,#{gym_addr} ,#{gym_addr_dtl} ,#{gym_zipcode}
                        ,#{gym_number} ,#{gym_info} ,#{gym_lat} ,#{gym_lng}
                        ,#{gym_parking}  ,#{gym_wash}  ,#{gym_uniform}  ,#{gym_locker}  ,0 )


	INSERT INTO class(
					  cls_no, gym_no, evt_seq, tch_no, type_no
					 ,cls_name, cls_kind, cls_s_date, cls_e_date
					 ,cls_cnt, cls_info, cls_price, cls_state
					 )
              VALUES(
              		  func_getclassNo(#{gym_no}),#{gym_no},#{ins_evt_seq},#{ins_tch_no},#{ins_type_no}
              		 ,#{ins_cls_name},#{ins_cls_kind},#{ins_cls_s_date},#{ins_cls_e_date}
              		 ,#{ins_cls_cnt},#{ins_cls_info},#{ins_cls_price},#{ins_cls_state}
              		 )

mapper.update:
UPDATE class 
   SET evt_seq = #{evt_seq}, tch_no = #{tch_no}, type_no = #{type_no}
      ,cls_name = #{cls_name}, cls_kind = #{cls_kind}, cls_s_date = #{cls_s_date}, cls_e_date = #{cls_e_date}
	  ,cls_cnt = #{cls_cnt}, cls_info = #{cls_info}, cls_price = #{cls_price}, cls_grcode = #{cls_grcode}, cls_state = #{cls_state}
 WHERE cls_no = #{cls_no}  
   AND gym_no = #{gym_no}

UPDATE notice set not_title = #{not_title}, not_cont = #{not_cont}
 WHERE notice_no = #{notice_no}
   AND gym_no = #{gym_no}

UPDATE gym set gym_pw = #{gym_pw}, gym_username = #{gym_username}
	 , gym_usertell = #{gym_usertell}, gym_tel = #{gym_tel}
	 , gym_addr = #{gym_addr}, gym_addr_dtl = #{gym_addr_dtl}
	 , gym_number = #{gym_number}, gym_info = #{gym_info}
	 , gym_parking = #{gym_parking}, gym_wash = #{gym_wash}
	 , gym_locker = #{gym_locker}, gym_uniform = #{gym_uniform}
 WHERE gym_no = #{gym_no}

mapper.delete:
DELETE notice
WHERE gym_no = #{gym_no}
  AND notice_no = #{notice_no}
